import React, {useContext} from 'react';
import SignIn from "./pages/SignIn";
import TextEditor from "./pages/TextEditor"
import Home from "./pages/Home";
import NavBar from "./pages/NavBar";
import './App.css'
import AuthProvider, {AuthContext} from "./pages/Authenticator";

import {BrowserRouter as Router, Routes, Route, Link, BrowserRouter}
    from 'react-router-dom';
function copyContent(){
    let button = document.getElementById("emailButton");
    navigator.clipboard.writeText(button.innerText);
    alert("Copied!");
}
function App() {
    const {userName} = useContext(AuthContext);

    return (
            <BrowserRouter>
                <div className="header">
                    {/* Link to the home page */}
                    <Link to="/">
                        <img className="logoLink" src="/images/Logo.png" alt="Logo" width="250" />
                    </Link>
                    <p id="userNameP">login info: </p>
                </div>

                {/* Render the appropriate component based on the URL */}
                <Routes>
                    <Route path="/" element={<NavBar />}>
                        {/* Render Home component when the URL matches / */}
                        <Route index element={<Home />} />
                        <Route path="text-editor" element={<TextEditor />} />
                        {userName === '' ? (
                            <Route path="sign-in" element={<SignIn />} />
                        ) : (
                            <Route path="sign-out" element={<SignIn />} />
                        )}
                        <Route className="signInRoute" path="sign-in" element={<SignIn />} />
                    </Route>
                </Routes>

                <div className="footer">
                    <p>Name : Diane Shin</p>
                    <p>Email : <button id="emailButton" className="emailButton" onClick={copyContent}>jadore845@snu.ac.kr</button></p>
                </div>
            </BrowserRouter>
    );
}

export default App;
