import {useEffect, useState} from "react";
import './Blog.css'
function Blog() {
    const [blogPosts, setBlogPosts] = useState(null);
    useEffect(() => {
        fetch("/fetchBlogPosts", {
            method: "GET",
            headers: {
                "Content-Type" : "application/json"
            }
        })
            .then((response) => {
                if (response.status === 200) return response.json();
                else throw new Error("No posts.")
            })
            .then((posts) => setBlogPosts(posts))
            .catch((error) => alert(error.message));
    }, []);


    return(
        <>
            <h2 id="blogTitle">Blog Posts</h2>
            {blogPosts && blogPosts.reverse().map((blogPosts) => (
                <div className="blogDiv" key={blogPosts.id}>
                    <h2 className="postTitle">{blogPosts.title}</h2>
                    <p className="postContent">{blogPosts.post}</p>
                    <p className="author">By : {blogPosts.email}</p>
                </div>
            ))}
        </>
    )
}

export default Blog;
