import {useContext, useEffect, useState} from "react";
import './Blog.css'
import {AuthContext} from "./Authenticator";
function Blog() {
    const [blogPosts, setBlogPosts] = useState(null);
    const [userEmail] = useContext(AuthContext);
    useEffect(() => {
        fetch("/fetchBlogPosts", {
            method: "GET",
            headers: {
                "Content-Type" : "application/json"
            }
        })
            .then((response) => {
                if (response.status === 200) return response.json();
                else throw new Error("No posts.")
            })
            .then((posts) => setBlogPosts(posts))
            .catch((error) => alert(error.message));
    }, []);

    function handleDelete(props){
        const data = {
            id : props.id,
            email : userEmail
        };
        fetch("/deleteBlogPost", {
            method: "DELETE",
            headers: {
                "Content-Type" : "application/json"
            },
            body: JSON.stringify(data)
        })
        .then((response) => response.text()
        )
        .then((text) => {
            if (text === "OK") alert("Post deleted.");
            else alert(text);
        })
        .catch((error) => alert("Something didn't go right."))
    }

    return(
        <>
            <h2 id="blogTitle">Blog Posts</h2>
            {blogPosts && blogPosts.reverse().map((blogPosts) => (
                <div className="blogDiv" key={blogPosts.id}>
                    <h2 className="postTitle">{blogPosts.title}</h2>
                    <p className="postContent">{blogPosts.post}</p>
                    <p className="author">By : {blogPosts.email}</p>
                    <button onClick={() => handleDelete(blogPosts.id)}>Delete Post</button>
                </div>
            ))}
        </>
    )
}

export default Blog;
