import {useContext} from "react";
import {AuthContext} from "../auth/Authenticator";



export const Comment = (props) => {
    const {userId} = useContext(AuthContext);

    function fetchComments() {
        fetch("/fetchCommentsForPostId", {

        })
    }
    function handleCommentPost() {
        const titleElem = document.getElementById("commentTitleText");
        const contentElem = document.getElementById("commentContentText");

        const newComment = {
            postId: props.postId,
            userId: userId,
            title: titleElem.value,
            content: contentElem.value
        }

        fetch("/uploadComment", {
            method: "POST",
            headers: {
                "Content-Type" : "application/json",
            },
            body : JSON.stringify(newComment)
        })
            .then((response) => response.text())
            .then((text) => {
                alert(text);
            });
    }

    return(
        <div>
            <textarea id = "commentTitleText" placeholder="Title"></textarea>
            <textarea id = "commentContentText" placeholder="Comment"></textarea>
            <button onClick={handleCommentPost}>Submit</button>
        </div>
    )
}

export default Comment;
