import React, {createContext, useEffect, useState} from "react";

export const AuthContext = createContext();

export default ({children}) => {
    const [userName, setUserName] = useState("");
    const [userEmail, setUserEmail] = useState("");

    useEffect(() => {
        const signInNavBarLink = document.getElementById("signInNavBarLink");
        const signInRoute = document.getElementById("signInRoute");

        if (userName === ""){
            signInNavBarLink.innerText = `Sign In`;
        }
        else{
            const userNameP = document.getElementById("userNameP");
            userNameP.innerText = `login info: ${userEmail}`;
            signInNavBarLink.innerText = `Sign Out`;
            signInNavBarLink.setAttribute("to", "/sign-out");
            signInRoute.setAttribute("path", "sign-out");
        }


    }, [userEmail, userName]);

    return(
        <AuthContext.Provider
            value={{
                userName,
                setUserName,
                userEmail,
                setUserEmail
            }}>
            {children}
        </AuthContext.Provider>
    );
};
